# This lets us slow down text so it is easier to read
import sys
import time
def slowprint(s):
    for c in s + '\n':
        sys.stdout.write(c)
        sys.stdout.flush()
        time.sleep(1./10)

# This let's us import sounds from our audio file
from pygame import mixer
# starting the mixer
mixer.init()
# loading the song
mixer.music.load('audio/opening.wav')
# setting the volume
mixer.music.set_volume(0.7)
# start playing the song
mixer.music.play(-1)
# This makes the sound clip a variable and is how we create a sound effect to add to actions/interactions/functions
test_sound = mixer.Sound("audio/gameover.wav")
kick_sound = mixer.Sound("audio/kick.wav")
evil_laugh = mixer.Sound("audio/nemesislaugh.wav")

